func (c *current) onQuery1(sels_, tail interface{}) (interface{}, error) {
	sels := toList(sels_)
	length := len(sels)
	var last selector
	if tail != nil {
		last = tail.(selector)
	}
	if length == 0 {
		return (&relSel{count: 1, next: last}), nil
	}
	for i := length - 1; i >= 0; i-- {
		sel := sels[i].(selector)
		sel.setNext(last)
		last = sel
	}
	switch first := sels[0].(type) {
	case *dirSel:
		Log("Parser: added root op")
		if first.count == 1 {
			sel := &rootSel{}
			sel.setNext(last)
			last = sel
		}
	case *patternSel:
		Log("Parser: added dir op")
		sel := &dirSel{}
		sel.setNext(last)
		last = sel
	}
	return last, nil
}

func (p *parser) callonQuery1() (interface{}, error) {
	stack := p.vstack[len(p.vstack)-1]
	_ = stack
	return p.cur.onQuery1(stack["sels_"], stack["tail"])
}

func (c *current) onExpression1(first, rest_ interface{}) (interface{}, error) {
	left := first.(fExpr)
	rest := toList(rest_)
	for _, comp_ := range rest {
		comp := toList(comp_)
		node := comp[0].(*orNode)
		node.left = left
		node.right = comp[1].(fExpr)
		left = node
	}
	return left, nil
}

func (p *parser) callonExpression1() (interface{}, error) {
	stack := p.vstack[len(p.vstack)-1]
	_ = stack
	return p.cur.onExpression1(stack["first"], stack["rest_"])
}

